# https://www.hackerrank.com/challenges/merge-the-tools/problem


def merge_the_tools(string, k):
    for i in range(0, len(string), k):
        s = ''
        for j in string[i:i + k]:
            if j not in s:
                s += j
        print(s)


in_string = ''
# in_string = 'AABCAAADA'
in_k = 1
merge_the_tools(in_string, in_k)
